import { Box, styled, Typography } from "@mui/material";
import React from "react";
import reactImg from "../media/react_svg.svg";

const Hero = () => {

  const CustomTitle = styled(Typography)(({ theme }) => ({
    color: "white",
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "3.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "2.5rem",
    },
  }));

  return (
    <Box
      sx={{
        mt: 8,
        mb: 12,
        p: 2,
        maxWidth: "1200px",
        mx: "auto",
      }}
      id="home"
    >
      <CustomTitle variant="h1">
      <span style={{ color: "rgba(255, 247, 0, 0.8)" }}>Portfolio</span>
      </CustomTitle>

        <Box
          component="img"
          sx={{
            height: 150,
            width: 150,
            mt: 4,
            mb: 4,
          }}
          src={reactImg}
        />

      </Box>
  );
};

export default Hero;
