import { Box, styled, Typography, Grid, Paper, List, ListItem, Avatar } from "@mui/material";
import React from "react";
import PhotoMe from "../../src/media/me.JPG"


const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: "black",
  padding: theme.spacing(1),
  textAlign: 'center',
  color: "gold"
}));


const Hero = () => {

  const CustomTitle = styled(Typography)(({ theme }) => ({
    color: "gold",
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "3.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "2.5rem",
    },
  }));

  return (
    <Box
      sx={{
        mt: 8,
        mb: 12,
        p: 2,
        maxWidth: "1200px",
        mx: "auto",
      }}
      id="home"
    >
      <CustomTitle variant="h1">
      <span style={{ color: "rgba(255, 247, 0, 0.8)" }}>Portfolio</span>
      </CustomTitle>

  <Grid container rowSpacing={0} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
  <Grid  xs={6}>
  <Avatar
  alt="AHarikaran"
  src={PhotoMe}
  sx={{ width: 400, height: 400 }}
/>
  </Grid>
-
</Grid>
</Box>
  );
};

export default Hero;
